// --- Paso 1: Obtener el texto de confirmación y los datos del nodo inicial ---

// Texto de confirmación del agente (nuestro input directo)
const textoConfirmacion = items[0].json.output;

// Nodo "Recibe Mensaje" para obtener el NÚMERO ESTRUCTURADO
// ¡VERIFICA QUE EL NOMBRE "Recibe Mensaje" SEA EXACTAMENTE IGUAL!
const recibeMensajeNode = $('Recibe Mensaje');


// --- Paso 2: Inicializamos las variables ---
let fecha = "no encontrada";
let hora = "no encontrada";
let nombre = "no especificado";
let apellido = "no especificado";
let telefono = "no encontrado";
let correo = "no tiene correo";


// --- Paso 3: Extraer datos del texto de confirmación del Agente ---

// Extraer fecha
const fechaMatch = textoConfirmacion.match(/para el (\d+ de \w+)/i);
if (fechaMatch && fechaMatch[1]) {
  fecha = fechaMatch[1];
}

// Extraer hora
const horaMatch = textoConfirmacion.match(/a las (\d{1,2}:\d{2} (?:AM|PM))/i);
if (horaMatch && horaMatch[1]) {
  hora = horaMatch[1];
}

// Extraer nombre completo
const nombreMatch = textoConfirmacion.match(/a nombre de ([\wáéíóúÁÉÍÓÚñÑ\s]+)\./i);
if (nombreMatch && nombreMatch[1]) {
  const nombreCompleto = nombreMatch[1].trim().split(' ');
  nombre = nombreCompleto[0];
  if (nombreCompleto.length > 1) {
    apellido = nombreCompleto.slice(1).join(' ');
  }
}

// --- Paso 4: Extraer el teléfono del CAMPO ESTRUCTURADO ---
if (recibeMensajeNode && recibeMensajeNode.item && recibeMensajeNode.item.json && recibeMensajeNode.item.json.numero) {
  telefono = recibeMensajeNode.item.json.numero;
}


// --- Paso 5: Devolvemos el objeto JSON con todos los datos recopilados ---
return [{
  json: {
    fecha: fecha,
    hora: hora,
    nombre: nombre,
    apellido: apellido,
    correo: correo,
    telefono: telefono
  }
}];
