// CORRECCIÓN: Se busca "mensaje" en lugar de "message".
const textInput = items.find(item => item.json.mensaje !== undefined);
const audioInput = items.find(item => item.json.candidates?.[0]?.content?.parts?.[0]?.text);
const imageInput = items.find(item => item.json.content?.parts?.[0]?.text);

let mensajeUnificado = '';

// Verificamos cuál de las entradas tiene datos y extraemos el mensaje.
if (textInput) {
  // CORRECCIÓN: Se extrae el dato de "mensaje".
  mensajeUnificado = textInput.json.mensaje;

} else if (audioInput) {
  // CORRECCIÓN: Se añade "?." (optional chaining) para hacer el código más seguro.
  mensajeUnificado = audioInput.json.candidates?.[0]?.content?.parts?.[0]?.text;

} else if (imageInput) {
  // CORRECCIÓN: Se añade "?." (optional chaining) para hacer el código más seguro.
  mensajeUnificado = imageInput.json.content?.parts?.[0]?.text;
}

// Devolvemos un nuevo objeto con la variable estandarizada.
return [{
  json: {
    mensaje_usuario: mensajeUnificado
  }
}];

