// Obtenemos el único dato que necesitamos: el texto de confirmación final del agente.
const agenteOutput = $('Valida datos del cliente').item.json.output;

// 1. Extraer Fecha y Hora (esto ya funcionaba bien)
const fechaMatch = agenteOutput.match(/\d{4}-\d{2}-\d{2}/);
const horaMatch = agenteOutput.match(/\d{1,2}:\d{2}\s?(a\.m\.|p\.m\.)/i);

const fecha = fechaMatch ? fechaMatch[0] : "Fecha no encontrada";
const hora = horaMatch ? horaMatch[0] : "Hora no encontrada";

// 2. Extraer Nombre y Apellido (NUEVA LÓGICA MEJORADA)
// Buscamos el texto que viene DESPUÉS de la palabra "confirmada," y ANTES del signo de interrogación.
const nombreCompletoMatch = agenteOutput.match(/confirmada,\s(.*?)\./);

let nombre = "No especificado";
let apellido = "";

if (nombreCompletoMatch && nombreCompletoMatch[1]) {
  const nombreCompleto = nombreCompletoMatch[1].trim();
  const partesNombre = nombreCompleto.split(' ');
  nombre = partesNombre[0] || "No especificado";
  apellido = partesNombre.slice(1).join(' ') || "";
}

// 3. Extraer el Teléfono (¡También del mensaje original!)
// Para esto, SÍ necesitamos referenciar el nodo 'Recibe Mensaje' porque el teléfono no está en la respuesta final.
// Lo manejaremos de forma segura.
let telefono = "No encontrado";
try {
  telefono = $('Recibe Mensaje').item.json['numero'];
} catch (e) {
  // Si falla, el teléfono simplemente no se encontrará, pero el flujo no se romperá.
}

// 4. Definir Correo
const correo = "no tiene correo";

// Devolvemos el objeto JSON limpio.
return {
  items: [{
    json: {
      fecha: fecha,
      hora: hora,
      nombre: nombre,
      apellido: apellido,
      correo: correo,
      telefono: telefono
    }
  }]
};
