    "https://grupodksoluciones.com/imgbody/7.jpg",
    "https://grupodksoluciones.com/imgbody/8.jpg",
    "https://grupodksoluciones.com/imgbody/9.jpg",
    "https://grupodksoluciones.com/imgbody/10.jpg",
  ],
  ENVIAR_TABLA_MEDIDAS: [
    "https://grupodksoluciones.com/imgbody/11.jpg"
  ],
};

const resultados = [];

for (const marcador of marcadores) {
  const tipo = marcador.includes("VIDEO") ? "video" : "image";
  const urls = driveUrls[marcador] || [];

  for (const url of urls) {
    resultados.push({
      json: {
        tipo,
        media_url: url
      }
    });
  }
}

if (resultados.length === 0) {
  return [{ json: { error: "NO_FILES_FOUND" } }];
}

return resultados;

// 🎯 SOLUCIÓN DEFINITIVA - Basada en investigación de la comunidad n8n
// Maneja el problema confirmado de Redis GET devolviendo undefined

// 📥 OBTENER DATOS DEL INPUT DIRECTO (del nodo anterior)
const incomingMessage = $input.first().json;
const userPhone = incomingMessage.user_phone;
const messageText = incomingMessage.current_message;
const messageType = incomingMessage.message_type;
const userName = incomingMessage.user_name;
const timestamp = incomingMessage.timestamp;

console.log("=== DATOS DEL INPUT DIRECTO ===");
console.log("📱 Usuario:", userPhone);
console.log("💬 Mensaje:", messageText);
console.log("👤 Nombre:", userName);
console.log("📝 Tipo:", messageType);
console.log("🕐 Timestamp:", timestamp);

// 🔍 OBTENER DATOS DE REDIS DESDE EL NODO ANTERIOR ESPECÍFICAMENTE
let redisData;
let existingHistory;

try {
  // El nodo anterior es "Memoria Local" (Redis GET)
  // Pero necesitamos los datos del webhook que vienen por $input
  console.log("🔍 Input completo:", JSON.stringify($input.all(), null, 2));

  // Buscar datos de Redis en el contexto del nodo anterior
  const previousNodeData = $input.first().json;

  // Si existe memoryData del Redis GET
  if (previousNodeData && previousNodeData.memoryData) {
    redisData = previousNodeData.memoryData;
    console.log("📊 Redis Data encontrada:", JSON.stringify(redisData, null, 2));

    // PARSEAR SI ES STRING
    if (typeof redisData === 'string') {
      try {
        redisData = JSON.parse(redisData);
        console.log("✅ Redis data parseada desde string");
      } catch (e) {
        console.log("❌ Error parseando redis data:", e);
        redisData = null;
      }
    }
  } else {
    console.log("⚠️ No hay memoryData en el input - verificando estructura");
    console.log("🔍 Estructura del input:", Object.keys(previousNodeData));
    redisData = null;
  }

  // VERIFICAR SI HAY DATOS VÁLIDOS DE REDIS
  if (redisData && redisData.messages && Array.isArray(redisData.messages)) {
    existingHistory = {
      messages: redisData.messages,
      total_messages: redisData.total_messages || redisData.messages.length
    };
    console.log("✅ Historial existente cargado desde Redis:", JSON.stringify(existingHistory, null, 2));
  } else if (redisData && redisData.value && redisData.value !== null && redisData.value !== "null") {
    try {
      // Intentar parsear JSON si es string
      if (typeof redisData.value === 'string') {
        existingHistory = JSON.parse(redisData.value);
      } else {
        existingHistory = redisData.value;
      }

      console.log("✅ Historial existente parseado desde value:", JSON.stringify(existingHistory, null, 2));
    } catch (parseError) {
      console.log("❌ Error parseando JSON, creando nuevo historial:", parseError.message);
      existingHistory = { messages: [] };
    }
  } else {
    console.log("🆕 No hay datos previos válidos, creando nuevo historial");
    existingHistory = { messages: [] };
  }

} catch (error) {
  console.log("❌ Error accediendo a Redis data:", error.message);
  existingHistory = { messages: [] };
}

// 🧠 PROCESAR MENSAJE ACTUAL
const newMessage = {
  role: "user",
  content: messageText,
  timestamp: timestamp,
  user_phone: userPhone,
  user_name: userName,
  message_type: messageType
};

// 📚 ACTUALIZAR HISTORIAL
if (!existingHistory.messages) {
  existingHistory.messages = [];
}

existingHistory.messages.push(newMessage);

// 🔄 MANTENER SOLO ÚLTIMOS 10 MENSAJES (OPTIMIZACIÓN)
if (existingHistory.messages.length > 10) {
  existingHistory.messages = existingHistory.messages.slice(-10);
}

// 📤 PREPARAR DATOS PARA GUARDAR
const dataToSave = {
  user_phone: userPhone,
  messages: existingHistory.messages,
  last_updated: timestamp,
  total_messages: existingHistory.messages.length
};

console.log("💾 Datos preparados para Redis SET:", JSON.stringify(dataToSave, null, 2));

// 🎯 RETURN FINAL
return [{
  json: {
    user_phone: userPhone,
    current_message: messageText,
    redisData: dataToSave, // Para el nodo Redis SET
    conversationHistory: existingHistory.messages, // Para uso en próximos nodos
    timestamp: timestamp,
    debug: {
      redis_raw: redisData,
      redis_had_value: !!(redisData && redisData.value),
      messages_count: existingHistory.messages.length,
      operation: "memory_processed"
    }
  }
}];

// 🔧 NOTAS DE IMPLEMENTACIÓN:
// 1. Este código maneja el bug confirmado de Redis undefined
// 2. Usa múltiples verificaciones de seguridad 
// 3. Incluye logging detallado para debugging
// 4. Funciona tanto con datos nuevos como existentes

// Tomar el mensaje del Agente Luisa
const mensajeDelAgente = $node["Agente Laura"].json.output || "";

// Eliminar marcadores del tipo [[...]]
const mensajeLimpio = mensajeDelAgente.replace(/\[\[.*?\]\]/g, '').replace(/\s{2,}/g, ' ').trim();

// Tomar datos del Webhook
const wa_id = $('Webhook -- Wasapi').first().json.body.data.wa_id;
const from_id = $('Webhook -- Wasapi').first().json.body.data.from_id;

return [
  {
    json: {
      mensaje_final: mensajeLimpio,
      wa_id,
      from_id
    }
  }
];

// UNIFICAR MENSAJES DE AUDIO Y TEXTO PARA REDIS
const items = $input.all();
const unifiedData = [];

for (const item of items) {
  let unifiedMessage = {};

  // Detectar si viene de Audio o Texto
  const isAudioMessage = item.json.transcribed_text || item.json.audio_text || item.json.transcript;
  const isTextMessage = item.json.message || item.json.text || item.json.body;

  if (isAudioMessage) {
    // PROCESAR MENSAJE DE AUDIO
    unifiedMessage = {
      // Datos básicos del mensaje
      message_type: "audio",
      user_message: item.json.transcribed_text || item.json.audio_text || item.json.transcript || "",
      original_format: "audio",

      // Información del contacto
      phone_number: item.json.from || item.json.phone || "",
      contact_name: item.json.contact_name || item.json.name || "",

      // Metadatos WhatsApp
      message_id: item.json.id || item.json.message_id || "",
      timestamp: item.json.timestamp || new Date().toISOString(),

      // Información específica del audio
      audio_duration: item.json.duration || "",
      audio_format: item.json.format || "ogg",
      transcription_confidence: item.json.confidence || "",

      // Para Redis
      redis_key: `chat:${item.json.from || 'unknown'}`,
      session_id: item.json.from || item.json.phone || 'unknown_session',

      // Estado del procesamiento
      processed: true,
      processing_time: new Date().toISOString(),
      source_node: "audio_processing"
    };

  } else if (isTextMessage) {
    // PROCESAR MENSAJE DE TEXTO
    unifiedMessage = {
      // Datos básicos del mensaje
      message_type: "text",
      user_message: item.json.message || item.json.text || item.json.body || "",
      original_format: "text",

      // Información del contacto
      phone_number: item.json.from || item.json.phone || "",
      contact_name: item.json.contact_name || item.json.name || "",

      // Metadatos WhatsApp
      message_id: item.json.id || item.json.message_id || "",
      timestamp: item.json.timestamp || new Date().toISOString(),

      // Información específica del texto
      message_length: (item.json.message || item.json.text || "").length,
      contains_emoji: /(\u00a9|\u00ae|[\u2000-\u3300]|\ud83c[\ud000-\udfff]|\ud83d[\ud000-\udfff]|\ud83e[\ud000-\udfff])/.test(item.json.message || item.json.text || ""),

      // Para Redis
      redis_key: `chat:${item.json.from || 'unknown'}`,
      session_id: item.json.from || item.json.phone || 'unknown_session',

      // Estado del procesamiento
      processed: true,
      processing_time: new Date().toISOString(),
      source_node: "text_processing"
    };

  } else {
    // MENSAJE NO IDENTIFICADO
    unifiedMessage = {
      message_type: "unknown",
      user_message: "Mensaje no procesable",
      original_format: "unknown",
      phone_number: item.json.from || "unknown",
      contact_name: "Unknown Contact",
      message_id: item.json.id || "unknown_id",
      timestamp: new Date().toISOString(),
      redis_key: `chat:${item.json.from || 'unknown'}`,
      session_id: item.json.from || 'unknown_session',
      processed: false,
      error: "Message format not recognized",
      source_node: "unification_error",
      raw_data: item.json // Para debugging
    };
  }

  // Agregar datos adicionales comunes
  unifiedMessage.workflow_step = "message_unified";
  unifiedMessage.ready_for_redis = true;
  unifiedMessage.unified_at = new Date().toISOString();

  // Crear item unificado
  const unifiedItem = {
    json: unifiedMessage,
    binary: item.binary || {}
  };

  unifiedData.push(unifiedItem);
}

return unifiedData;
