const baseUrl = "https://grupodksoluciones.com/bienvenidabaquera/";
const total = 2;

let items = [];

for (let i = 1; i <= total; i++) {
  items.push({
    json: {
      nombre: `${i}.jpg`,
      url: `${baseUrl}${i}.jpg`
    }
  });
}

return items;

// Obtenemos la salida de texto del Agente Laura
const outputString = items[0].json.output;

// Valores por defecto por si la IA responde con texto plano
let finalAction = 'ACCION_ENVIAR_TEXTO';
let finalMessage = outputString;

// Expresi√≥n regular para buscar y extraer el valor de la clave "action"
const actionRegex = /"action":\s*"([^"]*)"/;
// Expresi√≥n regular para buscar y extraer el valor de la clave "message"
// Esta versi√≥n maneja texto de m√∫ltiples l√≠neas y caracteres especiales
const messageRegex = /"message":\s*"([\s\S]*?)"\s*}/;

const actionMatch = outputString.match(actionRegex);
const messageMatch = outputString.match(messageRegex);

// Si encontramos AMBOS valores, significa que la IA nos dio el JSON
if (actionMatch && messageMatch) {
  finalAction = actionMatch[1];
  // El [1] se refiere al texto capturado dentro de los par√©ntesis en el regex
  finalMessage = messageMatch[1];
}

// Devolvemos el objeto final, limpio y estructurado
return [{
  json: {
    action: finalAction,
    // Limpiamos la salida por si acaso, eliminando caracteres de escape
    message: finalMessage.replace(/\\n/g, '\n').trim()
  }
}];

// Obtenemos los datos de las tres posibles entradas (inputs).
const textInput = items.find(item => item.json.message !== undefined);
const audioInput = items.find(item => item.json.candidates?.[0]?.content?.parts?.[0]?.text);
// CORRECCI√ìN: La ruta para la imagen es m√°s directa, sin "candidates".
const imageInput = items.find(item => item.json.content?.parts?.[0]?.text);

let mensajeUnificado = '';

// Verificamos cu√°l de las entradas tiene datos y extraemos el mensaje.
if (textInput) {
  mensajeUnificado = textInput.json.message;
} 
else if (audioInput) {
  mensajeUnificado = audioInput.json.candidates[0].content.parts[0].text;
} 
else if (imageInput) {
  // CORRECCI√ìN: Usamos la ruta correcta para la imagen.
  mensajeUnificado = imageInput.json.content.parts[0].text;
}

// Devolvemos un nuevo objeto con la variable estandarizada.
return [{
  json: {
    mensaje_usuario: mensajeUnificado
  }
}];
