// Obtener el número desde el nodo "Mapea el Mensaje Usuario"
let numeroOriginal = $('Mapea el Mensaje Usuario').first().json.number || '';

// Convertir a string si no lo es
if (typeof numeroOriginal !== 'string') {
numeroOriginal = String(numeroOriginal);
}

// Limpiar el número de caracteres no numéricos
const numero = numeroOriginal.replace(/\D/g, '');

// Lista de contactos recibida desde el nodo "Get many contacts"
const contactos = items;

let existe = false;

for (const contacto of contactos) {
const telefonos = contacto.json.phoneNumbers?.mobile || [];
for (const tel of telefonos) {
const telLimpio = String(tel).replace(/\D/g, '');
if (telLimpio === numero) {
existe = true;
break;
}
}
if (existe) break;
}
// Devolver resultado para el nodo IF
return [{ json: { existe } }];

// Obtener datos desde el nodo "Extrae los Datos del Cliente"
const nombre = $node["Extrae los Datos del Cliente"].json.nombre;
const celular = $node["Extrae los Datos del Cliente"].json.celular;
const correo = $node["Extrae los Datos del Cliente"].json.correo;
const proyecto = $node["Extrae los Datos del Cliente"].json.proyecto;

return [
  {
    json: {
      nombre,
      celular,
      correo,
      proyecto
    }
  }
];

const texto = $json.output || "";
const marcadoresDisponibles = [
  "ENVIAR_IMAGENES_BOREAL",
  "ENVIAR_VIDEO_BOREAL",
  "ENVIAR_IMAGENES_NATTIVO",
  "ENVIAR_VIDEO_NATTIVO"
];

const marcadoresDetectados = marcadoresDisponibles.filter(m =>
  texto.includes(`[[${m}]]`)
);

if (marcadoresDetectados.length === 0) {
  return [
    {
      json: {
        resultado: "NO_MARKERS_FOUND",
        original: texto
      }
    }
  ];
}

return [
  {
    json: {
      marcadores: marcadoresDetectados
    }
  }
];

const marcadores = $json.marcadores || [];

const driveUrls = {
  ENVIAR_IMAGENES_BOREAL: [
    "https://drive.google.com/uc?id=1pfzeYiKLiodbotqQci5ZGUPcZeTk72jE",
    "https://drive.google.com/uc?id=1TgSosy157Kg0_GfsniQzwdargLjYn9Hg",
    "https://drive.google.com/uc?id=1DQ6ZFjPfNGNnF4PMDGTirAb1wDN8WC_0",
    "https://drive.google.com/uc?id=1loho1vB8GuDWYwTHyUDTQE37ZMVyaZA5",
    "https://drive.google.com/uc?id=1IrmC4fnfKeP1eQa1FWYkwIhjKhV80Sb4",
    "https://drive.google.com/uc?id=1MYIebTyAUGS_O0UoFDkCZZWsJdjJwF_2",
    "https://drive.google.com/uc?id=1dFplyLlvd0cZHw-gsueUYWNc_VlnPBpf",
    "https://drive.google.com/uc?id=16V88zZCc6Zfid3_gi9dW_DDF25mM53zw"
  ],
  ENVIAR_VIDEO_BOREAL: [
    "https://drive.google.com/uc?id=1G2B4Zfft0ZRNBogvRJzpmZLKYTXoElbT",
    "https://drive.google.com/uc?id=1ZbTheV1qbfYAezxpGO2M6zeta9BCstXk"
  ],
  ENVIAR_IMAGENES_NATTIVO: [
    "https://drive.google.com/uc?id=12HlGHSkFpfpep1aI_wJYlBfNPx6Pi0iK",
    "https://drive.google.com/uc?id=1Jspnwf7z9jzLPIIZiMJsXG4ofcy-r3d-",
    "https://drive.google.com/uc?id=1sizL9DrP9SY-OFX7zCiofwr-iTUmmreN",
    "https://drive.google.com/uc?id=1C4ISYT7lGX8ZngPeNOR34W1jDZy6pNCI",
    "https://drive.google.com/uc?id=1F1hMSdQhm0BA0tzH2nWYDkwO81SEMhKY",
    "https://drive.google.com/uc?id=1qtUZeYFqqW0yHH94AIUyS5yuiHHVr73v",
    "https://drive.google.com/uc?id=1s_GKNjuWXBn_KP3l7_Jr2iFMHoNnbF_K",
    "https://drive.google.com/uc?id=1Hb22rawrHeS0IPJw6OBPfA1VwntQHrfQ"
  ],
  ENVIAR_VIDEO_NATTIVO: [
    "https://drive.google.com/uc?id=1X-OsB5Acf0FQDem61_DNxaj1uDw4N_-c"
  ]
};

const resultados = [];

for (const marcador of marcadores) {
  const tipo = marcador.includes("VIDEO") ? "video" : "image";
  const urls = driveUrls[marcador] || [];

  for (const url of urls) {
    resultados.push({
      json: {
        tipo,
        media_url: url
      }
    });
  }
}

if (resultados.length === 0) {
  return [{ json: { error: "NO_FILES_FOUND" } }];
}

return resultados;

// Tomar el mensaje del Agente Luisa
const mensajeDelAgente = $node["Agente Simón"].json.output || "";

// Eliminar marcadores del tipo [[...]]
const mensajeLimpio = mensajeDelAgente.replace(/\[\[.*?\]\]/g, '').replace(/\s{2,}/g, ' ').trim();

// Tomar datos del Webhook
const wa_id = $('Webhook -- Wasapi').first().json.body.data.wa_id;
const from_id = $('Webhook -- Wasapi').first().json.body.data.from_id;

return [
  {
    json: {
      mensaje_final: mensajeLimpio,
      wa_id,
      from_id
    }
  }
];

const mensaje = $json.data.message;

const datos = {
    fecha: new Date().toLocaleDateString("es-CO"),
    nombre: mensaje.match(/nombre:\s*(.+)/i)?.[1]?.trim() || '',
    celular: mensaje.match(/celular:\s*(.+)/i)?.[1]?.trim() || '',
    hora: mensaje.match(/hora de cita:\s*(.+)/i)?.[1]?.trim() || '',
    correo: mensaje.match(/correo electrónico:\s*(.+)/i)?.[1]?.trim() || '',
    proyecto: mensaje.match(/Proyecto de su interés:\s*(.+)/i)?.[1]?.trim() || ''
};

return [{ json: datos }];

const fechaTexto = $node["Extrae los Datos del Cliente"].json.fecha; // "17/7/2025"
const horaTexto = $node["Extrae los Datos del Cliente"].json.hora;   // "9:00 AM"

// Convertimos a formato ISO usando Date.parse
const [dia, mes, anio] = fechaTexto.split("/");
let hora = horaTexto.trim().toUpperCase();

if (hora.includes("AM") || hora.includes("PM")) {
  const date = new Date(`${anio}-${mes.padStart(2, "0")}-${dia.padStart(2, "0")} ${hora}`);
  const startISO = date.toISOString();
  const endISO = new Date(date.getTime() + 60 * 60 * 1000).toISOString(); // +1 hora

  return [
    {
      start: startISO,
      end: endISO
    }
  ];
} else {
  return [{ error: "Formato de hora no válido. Asegúrate que sea como '9:00 AM'" }];
}
