// Extrae o interpreta la fecha del mensaje
function extraerFechaDesdeMensaje(texto) {
	// Buscar una fecha expl√≠cita tipo 17/07/2025
	const matchFecha = texto.match(/(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{2,4})/);
	if (matchFecha) {
		const [_, d, m, y] = matchFecha;
		return `${String(d).padStart(2, '0')}/${String(m).padStart(2, '0')}/${y.length === 2 ? '20' + y : y}`;
	}

	// Expresiones relativas
	if (texto.includes("pasado ma√±ana")) {
		const fecha = new Date(hoy.getTime());
		fecha.setDate(hoy.getDate() + 2);
		return formatearFecha(fecha);
	}
	if (texto.includes("ma√±ana")) {
		const fecha = new Date(hoy.getTime());
		fecha.setDate(hoy.getDate() + 1);
		return formatearFecha(fecha);
	}
	if (texto.includes("hoy")) {
		return formatearFecha(hoy);
	}

	// D√≠a de la semana (s√°bado, domingo, etc.)
	const matchDia = texto.match(/\b(lunes|martes|mi√©rcoles|miercoles|jueves|viernes|s√°bado|sabado|domingo)\b/);
	if (matchDia) {
		const hoyDia = hoy.getDay();
		const diaObjetivo = diaSemanaANum(matchDia[1]);
		let diasADesplazar = (diaObjetivo - hoyDia + 7) % 7;
		if (diasADesplazar === 0) diasADesplazar = 7;
		const fecha = new Date(hoy.getTime());
		fecha.setDate(hoy.getDate() + diasADesplazar);
		return formatearFecha(fecha);
	}

	return '';
}

