  const [dia, mes, anio] = fechaTexto.split("/").map(v => parseInt(v));
  if (!dia || !mes || !anio) throw new Error("Fecha inv√°lida. Formato esperado: DD/MM/YYYY");

  // Normalizar la hora
  let horaLimpia = horaTexto.trim().toUpperCase();

  // Agregar minutos si solo viene la hora (ej. "9" ‚Üí "9:00")
  if (/^\d{1,2}$/.test(horaLimpia)) {
    horaLimpia += ":00";
  }

  // Construcci√≥n de fecha final seg√∫n formato
  let fechaHoraTexto;

  if (horaLimpia.includes("AM") || horaLimpia.includes("PM")) {
    // Formato con AM/PM ‚Üí MM/DD/YYYY HH:MM AM/PM
    fechaHoraTexto = `${mes}/${dia}/${anio} ${horaLimpia}`;
  } else {
    // Formato 24h ‚Üí YYYY-MM-DDTHH:MM:00
    fechaHoraTexto = `${anio}-${String(mes).padStart(2, '0')}-${String(dia).padStart(2, '0')}T${horaLimpia}:00`;
  }

  const startDate = new Date(fechaHoraTexto);
  if (isNaN(startDate)) throw new Error("No se pudo interpretar la fecha y hora. Revisa el formato.");

  const endDate = new Date(startDate.getTime() + 60 * 60 * 1000); // +1 hora

  return [{
    json: {
      start: startDate.toISOString(),
      end: endDate.toISOString()
