// Apuntamos a la ruta CORRECTA para obtener el texto del mensaje.
const mensaje = $input.item.json.content;

// Verificamos si el mensaje existe para evitar errores.
if (!mensaje) {
  return [{ json: { error: "El campo 'message' no se encontr√≥ en la entrada." } }];
}

const datos = {};

// --- Funci√≥n Auxiliar para Extraer Datos (mejorada para evitar errores) ---
function extraer(regex) {
  const match = mensaje.match(regex);
  return match ? match[1].trim() : '';
}

// --- Extracci√≥n de cada campo con NOMBRES CORREGIDOS y REGEX MEJORADAS ---
// Los nombres de las propiedades (ej: nombreCompleto) ahora coinciden con lo que
// espera el nodo de Google Sheets.
datos.nombreCompleto = extraer(/Nombre: (.*?)\n/i);
datos.telefono = extraer(/Tel√©fono: (.*?)\n/i);
datos.direccionCompleta = extraer(/Direcci√≥n: (.*?)\n/i);
datos.apartamento = extraer(/Apartamento: (.*?)\n/i);
datos.ciudad = extraer(/Ciudad: (.*?)\n/i);
datos.precio = extraer(/Precio: (.*?)\n/i);
// Para campos que no siempre est√°n, como Referencia y Color, esto devolver√° '' si no los encuentra.
datos.referenciaAdvocacion = extraer(/Referencia Advocaci√≥n: (.*?)\n/i);
datos.color = extraer(/Color: (.*?)\n/i);

// La fecha se puede generar al momento.
datos.fecha = new Date().toLocaleDateString('es-ES');

// Devolvemos los datos extra√≠dos listos para el nodo de Google Sheets.
return [{
  json: datos
}];

// Obtener el n√∫mero desde el nodo "Mapea el Mensaje Usuario"
let numeroOriginal = $('Mapea el Mensaje Usuario').first().json.number || '';

// Convertir a string si no lo es
if (typeof numeroOriginal !== 'string') {
numeroOriginal = String(numeroOriginal);
}

// Limpiar el n√∫mero de caracteres no num√©ricos
const numero = numeroOriginal.replace(/\D/g, '');

// Lista de contactos recibida desde el nodo "Get many contacts"
const contactos = items;

let existe = false;

for (const contacto of contactos) {
const telefonos = contacto.json.phoneNumbers?.mobile || [];
for (const tel of telefonos) {
const telLimpio = String(tel).replace(/\D/g, '');
if (telLimpio === numero) {
existe = true;
break;
}
}
if (existe) break;
}
// Devolver resultado para el nodo IF
return [{ json: { existe } }];

