const mensaje = $json.data.message;

const datos = {
  fecha: new Date().toLocaleDateString('es-CO'),

  // Extraer nombre usando múltiples patrones
  nombre: mensaje.match(/\*\*Cliente\*\*:\s*(.+?)(?:\n|\*\*|$)/i)?.[1]?.trim() || 
          mensaje.match(/Nombre[:\s]*(.+?)(?:\n|,|\.|$)/i)?.[1]?.trim() || 
          mensaje.match(/me llamo\s+([^,\n]+)/i)?.[1]?.trim() || "",

  // Extraer ciudad y departamento
  ciudad: mensaje.match(/\*\*Ciudad\*\*:\s*(.+?)(?:,|\n|\*\*|$)/i)?.[1]?.trim() || 
          mensaje.match(/Ciudad[:\s]*(.+?)(?:,|\n|$)/i)?.[1]?.trim() || 
          mensaje.match(/soy de\s+([^,\n]+)/i)?.[1]?.trim() || "",

  departamento: mensaje.match(/\*\*Ciudad\*\*:\s*[^,]+,\s*(.+?)(?:\n|\*\*|$)/i)?.[1]?.trim() || 
                mensaje.match(/Departamento[:\s]*(.+?)(?:\n|,|$)/i)?.[1]?.trim() || "",

  // Extraer dirección completa
  direccion: mensaje.match(/\*\*Dirección\*\*:\s*(.+?)(?:\n|\*\*|$)/i)?.[1]?.trim() || 
             mensaje.match(/Dirección[:\s]*(.+?)(?:\n|,|$)/i)?.[1]?.trim() || 
             mensaje.match(/(?:vivo en|mi dirección es)\s*(.+?)(?:\n|,|$)/i)?.[1]?.trim() || "",

  // Extraer teléfono
  telefono: mensaje.match(/\*\*Teléfono\*\*:\s*(.+?)(?:\n|\*\*|$)/i)?.[1]?.trim() || 
            mensaje.match(/Teléfono[:\s]*(.+?)(?:\n|,|$)/i)?.[1]?.trim() || 
            mensaje.match(/(?:mi teléfono|mi número).*?(\d{10})/i)?.[1] || 
            mensaje.match(/(\d{3}[-\s]?\d{3}[-\s]?\d{4})/)?.[1] || "",

  // Extraer cantidad
  cantidad: mensaje.match(/\*\*Cantidad\*\*:\s*(.+?)(?:\n|\*\*|$)/i)?.[1]?.trim() || 
            mensaje.match(/Cantidad[:\s]*(.+?)(?:\n|,|$)/i)?.[1]?.trim() || 
            mensaje.match(/quiero\s+(\d+)/i)?.[1] || "1",

  // Extraer presentación del producto
  presentacion: mensaje.match(/\*\*Producto\*\*:\s*Prostagen\s*(.+?)(?:\n|\*\*|$)/i)?.[1]?.trim() || 
                mensaje.match(/Presentación[:\s]*(.+?)(?:\n|,|$)/i)?.[1]?.trim() || 
                mensaje.match(/(\d+\s*litros?)/i)?.[1] || 
                mensaje.match(/(1L|2L)/i)?.[1] || "",

  // Extraer valor/precio
  valor: mensaje.match(/\*\*Valor\*\*:\s*\$?(.+?)(?:\n|\*\*|$)/i)?.[1]?.trim() || 
         mensaje.match(/Precio[:\s]*\$?(.+?)(?:\n|,|$)/i)?.[1]?.trim() || 
         mensaje.match(/\$(\d{1,3}(?:,\d{3})*(?:\.\d{2})?)/)?.[1] || "",

  // Extraer método de pago
  metodo_pago: mensaje.match(/\*\*Método de pago\*\*:\s*(.+?)(?:\n|\*\*|$)/i)?.[1]?.trim() || 
               mensaje.match(/pago[:\s]*(.+?)(?:\n|,|$)/i)?.[1]?.trim() || 
               (mensaje.includes("contra entrega") ? "Contra entrega" : 
                mensaje.includes("consignación") ? "Consignación" : ""),

  // Datos adicionales del contexto
  mensaje_completo: mensaje,
  timestamp: new Date().toISOString(),

  // Validaciones
  datos_completos: function() {
    return this.nombre && this.ciudad && this.direccion && this.telefono;
  }
};

// Limpiar datos vacíos y espacios extra
Object.keys(datos).forEach(key => {
  if (typeof datos[key] === 'string') {
    datos[key] = datos[key].replace(/\s+/g, ' ').trim();
  }
});

// Validar si la dirección está completa (debe tener calle/carrera)
const direccionCompleta = datos.direccion && 
  (datos.direccion.match(/(?:calle|carrera|cr|cl|avenida|av|diagonal|transversal)/i) ||
   datos.direccion.match(/\d+.*#.*\d+/));

datos.direccion_valida = direccionCompleta;

return {
  json: datos
};

// Obtener el número desde el nodo "Mapea el Mensaje Usuario"
let numeroOriginal = $('Mapea el Mensaje Usuario').first().json.number || '';

// Convertir a string si no lo es
if (typeof numeroOriginal !== 'string') {
numeroOriginal = String(numeroOriginal);
}

// Limpiar el número de caracteres no numéricos
const numero = numeroOriginal.replace(/\D/g, '');

// Lista de contactos recibida desde el nodo "Get many contacts"
const contactos = items;

let existe = false;

for (const contacto of contactos) {
const telefonos = contacto.json.phoneNumbers?.mobile || [];
for (const tel of telefonos) {
const telLimpio = String(tel).replace(/\D/g, '');
if (telLimpio === numero) {
existe = true;
break;
}
}
if (existe) break;
}
// Devolver resultado para el nodo IF
return [{ json: { existe } }];

// Obtener datos desde los nodos anteriores
const nombre = $node["Extrae los Datos del Pedido"].json.nombre;
const celular = $node["Mapea el Mensaje Usuario"].json.number;

return [
  {
    json: {
      nombre,
      celular
    }
  }
];

const baseUrl = "https://grupodksoluciones.com/videos/";
const texto = $json.output || "";
const marcadoresDisponibles = [
  "ENVIAR_TESTIMONIOS",
];

const marcadoresDetectados = marcadoresDisponibles.filter(m =>
  texto.includes(`[[${m}]]`)
);

if (marcadoresDetectados.length === 0) {
  return [
    {
      json: {
        resultado: "NO_MARKERS_FOUND",
        original: texto
      }
    }
  ];
}

return [
  {
    json: {
      marcadores: marcadoresDetectados
    }
  }
];

const marcadores = $json.marcadores || [];

const driveUrls = {
  ENVIAR_TESTIMONIOS: [
    "https://grupodksoluciones.com/videos/testimonio_1.mp4",
    "https://grupodksoluciones.com/videos/testimonio_2.mp4"
  ],
};

const resultados = [];

for (const marcador of marcadores) {
  const tipo = marcador.includes("VIDEO") ? "video" : "image";
  const urls = driveUrls[marcador] || [];

  for (const url of urls) {
    resultados.push({
      json: {
        tipo,
        media_url: url
      }
    });
  }
}

if (resultados.length === 0) {
  return [{ json: { error: "NO_FILES_FOUND" } }];
}

return resultados;

// Tomar el mensaje del Agente Luisa
const mensajeDelAgente = $node["Agente Camila"].json.output || "";

// Eliminar marcadores del tipo [[...]]
const mensajeLimpio = mensajeDelAgente.replace(/\[\[.*?\]\]/g, '').replace(/\s{2,}/g, ' ').trim();

// Tomar datos del Webhook
const wa_id = $('Webhook -- Wasapi').first().json.body.data.wa_id;
const from_id = $('Webhook -- Wasapi').first().json.body.data.from_id;

return [
  {
    json: {
      mensaje_final: mensajeLimpio,
      wa_id,
      from_id
    }
  }
];
