// Obtener los datos del nodo anterior
const imageData = $input.first().json.data;
const mimeType = $input.first().json.mime || "image/png";
const fileName = $input.first().json.fileName || "imagen.png";

// Limpiar el prefijo data:image si existe
const base64Clean = imageData.replace(/^data:image\/[a-z]+;base64,/, '');

// Crear el binary data correcto para n8n
items[0].binary = {
  image: {  // Usamos "image" como nombre de la propiedad
    data: Buffer.from(base64Clean, 'base64'),
    mimeType: mimeType,
    fileName: fileName
  }
};

// Limpiar el JSON para evitar confusi√≥n
delete items[0].json.binary;

return items;

